// #26703: Get selected index
function getSelectedIndex(select) {
	
	var returnValue = -1;
	var selectedOption = select.parent().find('div').text();
	
	$.each(select.find('option'), function (index, option) {
		if (option.text == selectedOption) {
			returnValue = index;
			
			// Exit loop
			return false;
		}
	});
	
	return returnValue;
}

